import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;

public class StudentAverage {

    public static void main(String[] args) {
        String csvFile = "students.csv";
        String line;
        int totalAge = 0;
        int totalGrade = 0;
        int count = 0;

        try (BufferedReader br = new BufferedReader(new FileReader(csvFile))) {
            while ((line = br.readLine()) != null) {
                String[] studentData = line.split(",");

                if (studentData.length < 3) {
                    System.out.println("Skipping row with missing data: " + line);
                    continue;
                }

                try {
                    int age = Integer.parseInt(studentData[1].trim());
                    int grade = Integer.parseInt(studentData[2].trim());

                    totalAge += age;
                    totalGrade += grade;
                    count++;
                } catch (NumberFormatException e) {
                    System.out.println("Skipping row with invalid data: " + line);
                }
            }

            double averageAge = (double) totalAge / count;
            double averageGrade = (double) totalGrade / count;

            System.out.printf("Average Age: %.2f%n", averageAge);
            System.out.printf("Average Grade: %.2f%n", averageGrade);
        } catch (IOException e) {
            System.err.println("Error reading file: " + e.getMessage());
        }
    }
}

function reverseEverySecondChar(str) {
    // Split the string into an array of characters
    let arr = str.split('');

    // Iterate over every second character in the array
    for(let i = 1; i < arr.length; i += 2){
        // Reverse the character by converting it to a unicode value, subtracting it from the sum of 'z' and 'a', then converting it back to a character
        arr[i] = String.fromCharCode('z'.charCodeAt(0) + 'a'.charCodeAt(0) - arr[i].charCodeAt(0));
    }

    // Join the array back into a string and return it
    return arr.join('');
}

import twitter4j.*;
import java.util.List;

public class FetchTweets {

    public static void main(String[] args) {
        // Set up the Twitter instance with your credentials
        ConfigurationBuilder cb = new ConfigurationBuilder();
        cb.setDebugEnabled(true)
                .setOAuthConsumerKey("YOUR_CONSUMER_KEY")
                .setOAuthConsumerSecret("YOUR_CONSUMER_SECRET")
                .setOAuthAccessToken("YOUR_ACCESS_TOKEN")
                .setOAuthAccessTokenSecret("YOUR_ACCESS_TOKEN_SECRET");

        TwitterFactory tf = new TwitterFactory(cb.build());
        Twitter twitter = tf.getInstance();

        try {
            // Fetch the latest 10 tweets from a given user's timeline
            String username = "example_user"; // Replace this with the desired username
            List<Status> statuses = twitter.getUserTimeline(username, new Paging(1, 10));

            System.out.println("Showing @" + username + "'s latest 10 tweets:");
            for (Status status : statuses) {
                System.out.println(status.getCreatedAt() + " - " + status.getText());
            }
        } catch (TwitterException te) {
            te.printStackTrace();
            System.out.println("Failed to fetch tweets: " + te.getMessage());
            System.exit(-1);
        }
    }
}

