// Function to calculate the sum of all numbers up to n
function sumUpTo(n) {
    // Initialize total as 0
    var total = 0;

    // Loop from 1 to n (inclusive)
    for(var i = 1; i <= n; i++) {
        // Add current number to total
        total += i;
    }

    // Return the total
    return total;
}

function arrayStats(arr) {
  // Check if input is an array
  if (!Array.isArray(arr)) {
    throw new Error("Input must be an array");
  }

  let min = Infinity;
  let max = -Infinity;
  let sum = 0;
  let count = 0;

  // Iterate through the array elements
  for (let i = 0; i < arr.length; i++) {
    const num = arr[i];

    // Check if each element is an integer
    if (typeof num !== "number" || !Number.isInteger(num)) {
      throw new Error("All elements in the array must be integers");
    }

    // Update min, max, and sum values
    min = Math.min(min, num);
    max = Math.max(max, num);
    sum += num;
    count++;
  }

  // Return the calculated values as an object
  return {
    min: min,
    max: max,
    sum: sum,
    average: sum / count,
  };
}

package main

import (
	"fmt"
	"strconv"
	"os"
)

// Recursive function to calculate factorial
func factorial(n int) int {
	if n == 0 {
		return 1
	}
	return n * factorial(n-1)
}

func main() {
	if len(os.Args) != 2 {
		fmt.Println("Usage: go run factorial.go <positive_integer>")
		os.Exit(1)
	}

	n, err := strconv.Atoi(os.Args[1])
	if err != nil || n < 0 {
		fmt.Println("Error: Please provide a valid positive integer")
		os.Exit(1)
	}

	result := factorial(n)
	fmt.Printf("Factorial of %d is %d\n", n, result)
}

import java.time.LocalDate;
import java.time.format.DateTimeFormatter;
import java.util.Scanner;

public class DayOfWeekCalculator {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.println("Enter a date (MM/DD/YYYY): ");
        String inputDate = scanner.nextLine();

        // Create a DateTimeFormatter with the desired format
        DateTimeFormatter formatter = DateTimeFormatter.ofPattern("MM/dd/yyyy");

        // Parse the input date using the formatter
        LocalDate date = LocalDate.parse(inputDate, formatter);

        // Print the day of the week for the given date
        System.out.println("The day of the week is: " + date.getDayOfWeek());
    }
}

