package main

import (
	"fmt"
	"net/http"
	"time"
)

func main() {
	// Defining the handler function for GET request
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		if r.Method == "GET" { // Checking if the request method is GET
			currentTime := time.Now().Format(time.RFC1123) // Getting current time
			fmt.Fprintf(w, currentTime) // Writing the current time to the response writer
		}
	})

	// Starting the server on port 8080
	err := http.ListenAndServe(":8080", nil)

	// Error handling for server start failure
	if err != nil {
		fmt.Printf("Server start failed: %s\n", err)
	}
}
