import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;

public class EvenNumbersSum {

    public static void main(String[] args) {
        if (args.length != 1) {
            System.err.println("Usage: java EvenNumbersSum <filename>");
            System.exit(1);
        }

        String filename = args[0];
        int sumOfEvenNumbers = 0;

        // Read the file and calculate the sum of even numbers
        try (BufferedReader reader = new BufferedReader(new FileReader(filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] numbers = line.split("\\s+");
                for (String number : numbers) {
                    int num = Integer.parseInt(number);
                    if (num % 2 == 0) {
                        sumOfEvenNumbers += num;
                    }
                }
            }
        } catch (IOException e) {
            System.err.println("Error reading file: " + e.getMessage());
            System.exit(1);
        }

        // Print the result to the console
        System.out.println("The sum of even numbers is: " + sumOfEvenNumbers);
    }
}

package main

import (
	"encoding/json"
	"fmt"
	"net/http"
	"time"
)

type TimeResponse struct {
	Time string `json:"time"`
}

// currentTimeHandler handles incoming HTTP requests and responds with the current system time in JSON format
func currentTimeHandler(w http.ResponseWriter, r *http.Request) {
	currentTime := time.Now().Format(time.RFC3339)
	response := TimeResponse{Time: currentTime}

	w.Header().Set("Content-Type", "application/json")
	json.NewEncoder(w).Encode(response)
}

func main() {
	http.HandleFunc("/current-time", currentTimeHandler)

	fmt.Println("Server is listening on port 8080...")
	err := http.ListenAndServe(":8080", nil)
	if err != nil {
		fmt.Printf("Error starting server: %v\n", err)
	}
}

def is_palindrome(s):
    length = len(s)
    for i in range(length // 2):
        if s[i] != s[length - i - 1]:
            return False
    return True

# Test cases
