// Function to count the number of vowels in a string
function countVowels(str) {
    // Regular expression to match vowels
    var vowelPattern = /[aeiou]/gi;

    // Match the string with the pattern and get an array of matches
    var matchedVowels = str.match(vowelPattern);

    // If there are no matches, return 0. Otherwise, return the length of the array.
    return (matchedVowels === null) ? 0 : matchedVowels.length;
}

