function countFrequency(arr) {
  const freqObj = {};

  // Count the frequency of each integer in the array
  for (let num of arr) {
    if (freqObj.hasOwnProperty(num)) {
      freqObj[num]++;
    } else {
      freqObj[num] = 1;
    }
  }

  // Sort the object by frequency in descending order
  const sortedFreqObj = Object.entries(freqObj).sort((a, b) => b[1] - a[1]).reduce((obj, [key, value]) => {
    obj[key] = value;
    return obj;
  }, {});

  return sortedFreqObj;
}

// Example usage:
const arr = [3, 5, 2, 3, 6, 7, 2, 3, 8, 5];
console.log(countFrequency(arr));

// Function to reverse words in a sentence
function reverseWords(sentence) {
    // Split the sentence into an array of words
    var words = sentence.split(' ');

    // Reverse each word in the array
    for (var i = 0; i < words.length; i++) {
        words[i] = words[i].split('').reverse().join('');
    }

    // Join the reversed words back into a sentence
    return words.join(' ');
}

// Example usage:
console.log(reverseWords("Hello World")); // Outputs: "olleH dlroW"

