type Employee struct {
	Name  string `json:"name"`
	JobTitle string `json:"job_title"`
	Salary  int  `json:"salary"`
}

func main() {
	jsonFile, err := os.Open("employees.json")
	if err != nil {
		fmt.Println(err)
	}
	defer jsonFile.Close()

	byteValue, _ := ioutil.ReadAll(jsonFile)

	var employees []Employee
	json.Unmarshal(byteValue, &employees)

	totalPayrollCost := 0
	highestPaidEmployee := Employee{}
	for _, emp := range employees {
		totalPayrollCost += emp.Salary
		if emp.Salary > highestPaidEmployee.Salary {
			highestPaidEmployee = emp
		}
	}

	fmt.Printf("Total Payroll Cost: $%d\n", totalPayrollCost)
	fmt.Printf("Highest Paid Employee: %s with Salary $%d\n", highestPaidEmployee.Name, highestPaidEmployee.Salary)
}

def check_prime(n):
    # if the number is less than or equal to 1, it's not prime
    if n <= 1:
        return False

    # 2 and 3 are prime numbers
    if n <= 3:
        return True

    # check if the number is divisible by 2 or 3
    if n % 2 == 0 or n % 3 == 0:
        return False

    i = 5
    while(i * i <= n):
        # check if the number is divisible by any number up to its square root
        if (n % i == 0 or n % (i + 2) == 0):
            return False
        i += 6

    return True

def merge_sort(arr):
    if len(arr) <= 1:
        return arr

    mid = len(arr) // 2
    left = arr[:mid]
    right = arr[mid:]

    left = merge_sort(left)
    right = merge_sort(right)

    return merge(left, right)


def merge(left, right):
    result = []
    i = j = 0

    while i < len(left) and j < len(right):
        if left[i] < right[j]:
            result.append(left[i])
            i += 1
        else:
            result.append(right[j])
            j += 1

    result.extend(left[i:])
    result.extend(right[j:])

    return result

package main

import (
	"bufio"
	"fmt"
	"os"
	"strings"
)

func main() {
	if len(os.Args) < 2 {
		fmt.Println("Please provide a file path as a command-line argument.")
