function arrayStats(arr) {
  // Check if input is an array
  if (!Array.isArray(arr)) {
    throw new Error("Input must be an array");
  }

  let min = Infinity;
  let max = -Infinity;
  let sum = 0;
  let count = 0;

  // Iterate through the array elements
  for (let i = 0; i < arr.length; i++) {
    const num = arr[i];

    // Check if each element is an integer
    if (typeof num !== "number" || !Number.isInteger(num)) {
      throw new Error("All elements in the array must be integers");
    }

    // Update min, max, and sum values
    min = Math.min(min, num);
    max = Math.max(max, num);
    sum += num;
    count++;
  }

  // Return the calculated values as an object
  return {
    min: min,
    max: max,
    sum: sum,
    average: sum / count,
  };
}

// Function to convert decimal number to binary
function decimalToBinary(decimal) {
    // Check if the input is a valid number
    if (isNaN(decimal)) {
        return 'Invalid Input';
    }

    // Convert the decimal number to binary and return it
    return (decimal >>> 0).toString(2);
}

