def is_prime(n):
    # If the number is less than 2, it's not a prime number
    if n < 2:
        return False
    # Check each number up to the square root of n
    for i in range(2, int(n**0.5) + 1):
        # If n is divisible by any number up to its square root, it's not a prime number
        if n % i == 0:
            return False
    # If we've made it this far without returning False, n must be a prime number
    return True

def generate_dict(n):
    # Initialize an empty dictionary
    result = {}

    # Iterate over the range from 1 to n (inclusive)
    for i in range(1, n+1):
        # Add a new key-value pair to the dictionary,
        # where the key is the current number and the value is its square
        result[i] = i**2

    # Return the resulting dictionary
    return result

