function kadanesAlgorithm(array) {
  let maxCurrent = array[0];
  let maxGlobal = array[0];

  for (let i = 1; i < array.length; i++) {
    maxCurrent = Math.max(array[i], maxCurrent + array[i]);
    maxGlobal = Math.max(maxGlobal, maxCurrent);
  }

  return maxGlobal;
}

