// Function to find the greatest common divisor (GCD) of two numbers
function gcd(a, b) {
    // If 'b' is 0, then 'a' is the GCD
    if (b === 0) {
        return a;
    }

    // Otherwise, recursively call the function with 'b' and the remainder of 'a' divided by 'b'
    else {
        return gcd(b, a % b);
    }
}

