import { S3Handler, S3Event } from 'aws-lambda';
import { S3 } from 'aws-sdk';
import * as sharp from 'sharp';

const s3 = new S3();
const THUMBNAIL_WIDTH = 100;
const THUMBNAIL_HEIGHT = 100;

export const handler: S3Handler = async (event: S3Event) => {
  const promises = event.Records.map(async (record) => {
    const originalKey = record.s3.object.key;
    const thumbnailKey = `thumbnail-${originalKey}`;

    // Get the image from S3
    const imageObject = await s3
      .getObject({
        Bucket: record.s3.bucket.name,
        Key: originalKey,
      })
      .promise();

    // Resize the image
    const resizedImage = await sharp(imageObject.Body as Buffer)
      .resize(THUMBNAIL_WIDTH, THUMBNAIL_HEIGHT)
      .toBuffer();

    // Save the thumbnail back to S3
    await s3
      .putObject({
        Bucket: record.s3.bucket.name,
        Key: thumbnailKey,
        Body: resizedImage,
        ContentType: 'image/jpeg',
      })
      .promise();
  });

  await Promise.all(promises);
};

import requests
from bs4 import BeautifulSoup
import xml.etree.ElementTree as ET
from urllib.parse import urljoin

def crawl(url, domain):
    visited_links = set()
    links_to_visit = [url]

    while links_to_visit:
        current_url = links_to_visit.pop(0)
        if current_url not in visited_links:
            try:
                response = requests.get(current_url)
                soup = BeautifulSoup(response.text, 'html.parser')

                for link in soup.find_all('a'):
                    href = link.get('href')
                    if href.startswith(domain) or not href.startswith('http'):
                        full_link = urljoin(domain, href)
                        if full_link not in visited_links:
                            links_to_visit.append(full_link)

                visited_links.add(current_url)
            except Exception as e:
                print(f"Error: {e}")

    return visited_links

def generate_sitemap_xml(links, output_file='sitemap.xml'):
    urlset = ET.Element("urlset", xmlns="http://www.sitemaps.org/schemas/sitemap/0.9")

    for link in links:
        url = ET.SubElement(urlset, "url")
        loc = ET.SubElement(url, "loc")
        loc.text = link

    tree = ET.ElementTree(urlset)
    tree.write(output_file, encoding='utf-8', xml_declaration=True)

if __name__ == "__main__":
    domain = input("Enter the domain (including http(s)://): ")
    crawled_links = crawl(domain, domain)
    generate_sitemap_xml(crawled_links)
    print("Sitemap.xml generated successfully.")

