function findPairsWithSum(arr, target) {
  let result = [];
  let seenNumbers = new Set();

  for (let num of arr) {
    let complement = target - num;
    if (seenNumbers.has(complement)) {
      // Sort the pair to ensure uniqueness in the result array
      let pair = [num, complement].sort((a, b) => a - b);
      let isUnique = true;

      // Check if the pair already exists in the result array
      for (let existingPair of result) {
        if (existingPair[0] === pair[0] && existingPair[1] === pair[1]) {
          isUnique = false;
          break;
        }
      }

      if (isUnique) {
        result.push(pair);
      }
    } else {
      seenNumbers.add(num);
    }
  }

  return result;
}

