// Function to convert the first letter of each word in a string to uppercase
function capitalizeFirstLetter(string) {
    // Split the input string into an array of words
    let words = string.split(' ');

    // Iterate over each word in the array
    for (let i = 0; i < words.length; i++) {
        // Capitalize the first letter of the current word, and add it back to the rest of the word
        words[i] = words[i].charAt(0).toUpperCase() + words[i].slice(1);
    }

    // Join the words back together into a single string, with spaces between them
    return words.join(' ');
}

function isPalindrome(str) {
  // Remove non-alphanumeric characters and convert to lowercase
  const cleanedStr = str.replace(/[^a-z0-9]/gi, '').toLowerCase();

  // Reverse the cleaned string
  const reversedStr = cleanedStr.split('').reverse().join('');

  // Check if the cleaned string is equal to its reverse
  return cleanedStr === reversedStr;
}

