class Stack:
    def __init__(self):
        self.items = []

    # Add an item to the stack
    def push(self, item):
        self.items.append(item)

    # Remove and return the top item from the stack
    def pop(self):
        if not self.is_empty():
            return self.items.pop()

    # Return the top item of the stack without removing it
    def peek(self):
        if not self.is_empty():
            return self.items[-1]

    # Check if the stack is empty
    def is_empty(self):
        return len(self.items) == 0

    # String representation of the stack
    def __str__(self):
        return str(self.items)

// Function to find the largest and smallest number in an array
function findLargestAndSmallest(arr) {
    // Initialize variables for largest and smallest numbers
    let largest = arr[0];
    let smallest = arr[0];

    // Loop through the array
    for (let i = 1; i < arr.length; i++) {
        // If current element is larger than the largest, update largest
        if (arr[i] > largest) {
            largest = arr[i];
        }
        // If current element is smaller than the smallest, update smallest
        else if (arr[i] < smallest) {
            smallest = arr[i];
        }
    }

    // Return an object with the largest and smallest numbers
    return {largest: largest, smallest: smallest};
}

