    var largest = arr[0];

    // Loop through each element in the array
    for (var i = 1; i < arr.length; i++) {
        // If current element is larger than largest, update largest
        if (arr[i] > largest) {
            largest = arr[i];
        }
    }

    // Return the largest number
    return largest;
}

const {Storage} = require('@google-cloud/storage');
const path = require('path');

// Path to your service account key file
const keyFilePath = path.join(__dirname, 'your-service-account-key-file.json');

// Your Google Cloud Storage bucket name
const bucketName = 'your-bucket-name';

const storage = new Storage({keyFilename: keyFilePath});

async function uploadFile(filePath) {
  const options = {
    destination: path.basename(filePath),
  };

  await storage.bucket(bucketName).upload(filePath, options);
  console.log(`${filePath} uploaded to ${bucketName}`);
}

async function listFiles() {
  const [files] = await storage.bucket(bucketName).getFiles();
  files.forEach(file => {
    console.log(file.name);
  });
}

async function downloadFile(fileName, destPath) {
  const options = {
    destination: destPath,
  };

  await storage.bucket(bucketName).file(fileName).download(options);
  console.log(`Downloaded ${fileName} to ${destPath}`);
}

async function deleteFile(fileName) {
  await storage.bucket(bucketName).file(fileName).delete();
  console.log(`Deleted ${fileName} from ${bucketName}`);
}

// Example usage:
uploadFile('path/to/local/file.txt');
listFiles();
downloadFile('file-to-download.txt', 'path/to/destination/folder/file.txt');
deleteFile('file-to-delete.txt');

def hanoi(n, source, target, auxiliary):
