#include <iostream>
#include <fstream>
#include <string>
#include <set>

int main() {
    std::ifstream inputFile("names.txt");
    std::set<std::string> uniqueNames;

    if (inputFile.is_open()) {
        std::string name;
        while (std::getline(inputFile, name)) {
            uniqueNames.insert(name);
        }
        inputFile.close();
    } else {
        std::cout << "Unable to open file" << std::endl;
        return 1;
    }

    std::cout << "Total number of unique names: " << uniqueNames.size() << std::endl;

    return 0;
}
