def sieve_of_eratosthenes(limit):
    primes = []
    sieve = [True] * (limit + 1)

    # Iterate through numbers up to the square root of the limit
    for num in range(2, int(limit**0.5) + 1):
        if sieve[num]:
            primes.append(num)

            # Mark multiples of the current number as non-prime
            for multiple in range(num*num, limit + 1, num):
                sieve[multiple] = False

    # Add remaining prime numbers to the list
    for num in range(int(limit**0.5) + 1, limit + 1):
        if sieve[num]:
            primes.append(num)

    return primes

import java.util.Scanner;

public class ReverseString {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.print("Enter a string: ");
        String input = scanner.nextLine();
        String reversed = reverse(input);
        System.out.println("Reversed string: " + reversed);
    }

    // Method to reverse the given string
    private static String reverse(String str) {
        char[] chars = new char[str.length()];
        int index = 0;
        for (int i = str.length() - 1; i >= 0; i--) {
            chars[index++] = str.charAt(i);
        }
        return new String(chars);
    }
}

